import {Component, OnInit} from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent{


  autoSelezionata: any;

  gestisciAccessoRiuscito() {
    this.mostraAuto = true;
    this.mostraLoginComponent = false;
  }

  mostraDettagliAuto(auto: any) {
    this.autoSelezionata = auto;
  }

  mostraAuto = false;
  mostraLoginComponent = true;


  toggleLoginRegistrazione() {
    this.mostraLoginComponent = !this.mostraLoginComponent;
  }


  compra(autoSelezionata: any) {
    fetch('http://localhost:1337/inserisci', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify(autoSelezionata),
    })
        .then(response => response.json())
        .then(json => {
          console.log('Data sent successfully:', json);
          // Puoi anche chiamare fetchData() qui per aggiornare i dati dopo l'invio
        })
        .catch(err => console.error("Request failed: ", err));

  }
}
<div class="sfon">
  <div *ngIf="mostraAuto; else mostraLogin">
    <auto (mostraDettagli)="mostraDettagliAuto($event)"></auto>
  </div>
  <ng-template #mostraLogin>
    <div *ngIf="mostraLoginComponent; else mostraRegistrazione">
      <login (accessoRiuscito)="gestisciAccessoRiuscito()"></login>
      <button class="btn btn-light toggle-button" (click)="toggleLoginRegistrazione()">
        {{ mostraLogin ? 'Registrati' : 'Login' }}
      </button>
    </div>
  </ng-template>
  <ng-template #mostraRegistrazione>
    <div *ngIf="!mostraLoginComponent" class="princ">
      <registrati></registrati>
      <button class="btn btn-light toggle-button" (click)="toggleLoginRegistrazione()">
        {{ mostraLogin ? 'Registrati' : 'Login' }}
      </button>
    </div>
  </ng-template>

  <div *ngIf="autoSelezionata" class="lato">
    <h2>Dettagli Auto</h2>
    <p>Marca: {{ autoSelezionata.marca }}</p>
    <p>Modello: {{ autoSelezionata.modello }}</p>
    <p>Costo: {{ autoSelezionata.costo }}</p>
    <p>Categoria: {{ autoSelezionata.categoria }}</p>
    <p>Potenza: {{ autoSelezionata.dat_tecnici.potenza }}</p>
    <p>Consumi: {{ autoSelezionata.dat_tecnici.consumi }}</p>

    <button class="btn btn-danger" (click)="compra(autoSelezionata)">Compra</button>
  </div>
</div>
