import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  mostraLogin: boolean = true;
}
<div *ngIf="mostraLogin; else mostraRegistrazione">
  <login></login>
</div>
<ng-template #mostraRegistrazione>
  <registrati></registrati>
</ng-template>
<div class="princ">

  <button class="btn btn-light"  (click)="mostraLogin = !mostraLogin">
    {{ mostraLogin ? 'Registrati' : 'Login' }}
  </button>
</div>
