import {Component, EventEmitter, OnInit, Output} from '@angular/core';

@Component({
  selector: 'auto',
  templateUrl: './auto.component.html',
  styleUrls: ['./auto.component.css']
})
export class AutoComponent implements OnInit{

    @Output() mostraDettagli = new EventEmitter<any>();

    onCardClick(auto: any) {
        this.mostraDettagli.emit(auto);
    }
  private url = "http://localhost:1337/prendi";

  data: any;

  ngOnInit() {
    fetch(this.url)
        .then(response => response.json())
        .then(json => {
          this.data = json;
          console.log(this.data)
        })
        .catch(err => console.log("request failed: ", err))
  }

}
<!-- Nel template del tuo componente "Auto" -->
<div *ngFor="let auto of data" class="card card-body myCard" (click)="onCardClick(auto)">
    <img [src]="'/assets/img/' + auto.marca + '.jpg'" alt="{{ auto.modello }}">
    <div class="auto-info">
        <h4>{{ auto.marca }} {{ auto.modello }}</h4>
        <p>Categoria: {{ auto.categoria }}</p>
        <p>Alimentazione: {{ auto.alimentazione }}</p>
    </div>
</div>
