import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  mostraAuto = false;
  mostraLoginComponent = true;

  gestisciAccessoRiuscito() {
    this.mostraAuto = true;
    this.mostraLoginComponent = false;
  }

  toggleLoginRegistrazione() {
    this.mostraLoginComponent = !this.mostraLoginComponent;
  }
}
<!-- Nel template del tuo componente padre -->
<div *ngIf="mostraAuto; else mostraLogin">
  <auto></auto>
</div>
<ng-template #mostraLogin>
  <div *ngIf="mostraLoginComponent; else mostraRegistrazione">
    <login (accessoRiuscito)="gestisciAccessoRiuscito()"></login>
  </div>
</ng-template>
<ng-template #mostraRegistrazione>
  <registrati></registrati>
</ng-template>
<div class="princ">
  <button class="btn btn-light" (click)="toggleLoginRegistrazione()">
    {{ mostraLogin ? 'Registrati' : 'Login' }}
  </button>
</div>
